INSERT INTO `features_cards` (`id`, `data`, `deleted_at`, `created_at`, `updated_at`) VALUES
	(1, '{"en": [{"text": "You can store your excess items or items at home in our storage centers in Ankara and Istanbul.", "title": "Item Storage", "img_url": "fa-solid fa-warehouse"}, {"text": "We provide transportation services for your belongings in closed box trucks between the addresses you specify. For corporate home-to-home transportation service, make an appointment for an expert at the call center at 444 1980.", "title": "House to House Transportation", "img_url": "fa-solid fa-boxes-packing"}, {"text": "For partial goods transportation service, transport your belongings within Ankara or to other provinces with the assurance of Doğançay Home to Home Transportation.", "title": "Partial Goods Transportation", "img_url": "fa-solid fa-truck-arrow-right"}], "ge": [{"text": "Sie können Ihre überschüssigen Artikel oder Artikel zu Hause in unseren Lagerzentren in Ankara und Istanbul lagern.", "title": "Artikellagerung", "img_url": "fa-solid fa-warehouse"}, {"text": "Wir bieten den Transport Ihrer Sachen in geschlossenen Kofferfahrzeugen zwischen den von Ihnen angegebenen Adressen an. Vereinbaren Sie für den Firmentransport von Haus zu Haus einen Termin mit einem Experten im Callcenter unter 444 1980.", "title": "Transport von Haus zu Haus", "img_url": "fa-solid fa-boxes-packing"}, {"text": "Für den Teiltransport von Gütern transportieren Sie Ihre Sachen innerhalb von Ankara oder in andere Provinzen mit der Zusicherung von Doğançay Home to Home Transportation.", "title": "Teilweiser Gütertransport", "img_url": "fa-solid fa-truck-arrow-right"}], "tr": [{"text": "Evinizde bulunan atmaya kıyamadığınız veya fazla malzemelerinizi Ankara ve İstanbul’da bulunan Eşya depolama merkezlerimizde saklayabilirsiniz.", "title": "Eşya Depolama", "img_url": "fa-solid fa-warehouse"}, {"text": "Eşyalarınızın Belirlediğiniz adresler arası kapalı kasa kamyonlar içerisinde taşıma hizmetini sağlamaktayız. Kurumsal evden eve nakliyat hizmeti için 444 1980 numaralı çağrı merkezinden ekzpersiz randevusu oluşturun.", "title": "Evden Eve Nakliyat", "img_url": "fa-solid fa-boxes-packing"}, {"text": "Parça eşya taşıma hizmeti için İster Ankara içi isterseniz de diğer illere Doğançay Evden Eve Nakliyat güvencesi ile eşyalarınızı taşıtın.", "title": "Parça Eşya Taşıma", "img_url": "fa-solid fa-truck-arrow-right"}]}', NULL, NULL, NULL);

