INSERT INTO `galeris` (`id`, `data`, `deleted_at`, `created_at`, `updated_at`) VALUES
	(1, '{"en": {"text": "Doğançay Transportation\'s gallery section showcases the unique transportation projects we have undertaken both internationally and domestically. Each photograph reflects the originality and excellence of our work. These images, proving that transportation is an art, narrate the stories behind each journey. Doğançay Transportation offers an experience beyond transportation; that\'s why we are different.", "title": "Gallery"}, "ge": {"text": "Der Galeriebereich von Doğançay Nakliyat präsentiert die einzigartigen Verkehrsprojekte, die wir in der Türkei und auf der ganzen Welt realisiert haben. Jedes Foto spiegelt die Einzigartigkeit und Exzellenz unserer Arbeit wider. Diese Bilder, die beweisen, dass Transport Kunst ist, erzählen die Geschichten hinter jeder Reise. Dogancay International Movers bietet eine Erfahrung, die über den Transport hinausgeht, und das ist der Grund, warum wir anders sind.", "title": "Galerie"}, "tr": {"text": "Doğançay Nakliyat\'ın galeri bölümü, dünya genelinde ve yurt içinde gerçekleştirdiğimiz benzersiz nakliyat projelerini sunuyor. Her bir fotoğraf, işimizin özgünlüğünü ve mükemmelliğini yansıtıyor. Taşımanın sanat olduğunu kanıtlayan bu görüntüler, her bir yolculuğunun ardında yatan hikayeleri anlatıyor. Nakliyatın ötesinde bir deneyim sunan Doğançay Nakliyat, işte bu yüzden farklıyız.", "title": "Galeri"}, "images": [{"alt": "galeri resim", "order": "1", "img_url": "assets/images/new/gallery/GFbCSqvg1HbWtsjdU2N7_1699601287.jpeg"}, {"alt": "galeri resim", "order": "2", "img_url": "assets/images/new/gallery/JqPjjixr3yi9PqRkI6aT_1699601302.jpeg"}, {"alt": "galeri resim", "order": "3", "img_url": "assets/images/new/gallery/tHBtqu2d2ZXD7w9oOn6i_1699601312.jpeg"}, {"alt": "galeri resim", "order": "4", "img_url": "assets/images/new/gallery/KhsCfrSfaDhSf1L6n0p7_1699601323.jpeg"}, {"alt": "galeri resim", "order": "5", "img_url": "assets/images/new/gallery/KKRvehqmehUmCmsvllJc_1699601352.jpeg"}, {"alt": "galeri resim", "orde