INSERT INTO `welcomes` (`id`, `data`, `deleted_at`, `created_at`, `updated_at`) VALUES
	(1, '{"en": {"data": [{"text": "Our services include transportation services for international companies, diplomatic personnel and private customers planning to move abroad.", "title": "Our Moving Services", "img_url": "assets/images/new/welcome/iTwAXaeTYeSmhsR7dTE6_1702018583.jpg"}, {"text": "All residential moving consist of lots of details, but an international moving comes with even more requirements and preparations.", "title": "Planning and Preparation", "img_url": "assets/images/new/welcome/YPYm85AgNOHxewDEkcNm_1702018629.jpeg"}, {"text": "We offer two types of storages to keep your belongings in the best conditions that meet you demands. One of them is Smart-Storage/Movable Storage solution and the other is Smart Storage/Self-Storage.", "title": "Storage", "img_url": "assets/images/new/welcome/1VjeBS1ZyAICnT1X4D4h_1702018649.png"}]}, "ge": {"data": [{"text": "Unser internationaler Transportservice deckt den internationalen Heim- und Büroverkehr von internationalen Unternehmen, diplomatischem Personal im Ausland und Zivilpersonen ab.", "title": "Unsere Transportdienstleistungen", "img_url": "assets/images/new/welcome/iTwAXaeTYeSmhsR7dTE6_1702018583.jpg"}, {"text": "Obwohl alle Umzugsvorgänge eine detaillierte Planung im Voraus erfordern, sind für den internationalen Haus- und Bürotransport weitaus mehr Vorbereitungen und Arbeiten erforderlich.", "title": "Planung und Vorbereitung", "img_url": "assets/images/new/welcome/YPYm85AgNOHxewDEkcNm_1702018629.jpeg"}, {"text": "Wir bieten zwei verschiedene Lagerungsdienste an, um Ihr Hab und Gut auf die beste Art und Weise und entsprechend Ihren Bedürfnissen zu lagern. Es handelt sich um Smart Warehouse/Self Storage und Smart Warehouse/Transportable Warehouse Services.", "title": "Lagerung", "img_url": "assets/images/new/welcome/1VjeBS1ZyAICnT1X4D4h_1702018649.png"}]}, "tr": {"data": [{"text": "Uluslararası taşıma hizmetimiz uluslararası firmaların, yurtdışına görevlendirilmiş diplomatik personelin ve sivil şahısların uluslararası ev ve ofis taşıma işlemlerini kapsamaktadır.", "title": "Taşıma Hizmetlerimiz", "img_url": "assets/images/new/welcome/iTwAXaeTYeSmhsR7dTE6_1702018583.jpg"}, {"text": "Bütün taşınma işlemleri önceden detaylı bir planlama gerektirirse de Uluslararası ev ve ofis taşımacılığında çok daha fazla hazırlık ve çalışma gerekmektedir.", "title": "Planlama Ve Hazırlık", "img_url": "assets/images/new/welcome/YPYm85AgNOHxewDEkcNm_1702018629.jpeg"}, {"text": "Eşyalarınızı en iyi şekilde ve ihtiyaçlarınıza göre depolamak için iki farkı depolama hizmeti sunuyoruz. Bunlar Akıllı depo/Self Storage ve Akıllı Depo/Taşınabilir Depo hizmetleridir.", "title": "Depolama", "img_url": "assets/images/new/welcome/1VjeBS1ZyAICnT1X4D4h_1702018649.png"}]}}', NULL, NULL, '2023-12-08 04:38:44');

