INSERT INTO `features_cards` (`id`, `data`, `deleted_at`, `created_at`, `updated_at`) VALUES
	(1, '{"en": [{"text": "We can store your additional goods that you do not want to throw away in our warehouses both in Ankara and Istanbul.", "title": "Storage", "img_url": "fa-solid fa-warehouse"}, {"text": "We offer moving of your belongings in closed box removal vans between the addresses you specify. Make an appointment from our Call Center by calling 444 1980 for free survey of your goods subject to move.", "title": "Residential Door to Door Moving", "img_url": "fa-solid fa-boxes-packing"}, {"text": "We can transport your parcel items not only in Ankara but also in everywhere in Turkey.", "title": "Partial Goods Transportation", "img_url": "fa-solid fa-truck-arrow-right"}], "ge": [{"text": "Sie können Ihre überschüssigen Materialien bei sich zu Hause lagern, die Sie nicht wegwerfen können, oder Sie können Ihre überschüssigen Materialien in unseren Warenlagerzentren in Ankara und Istanbul lagern.", "title": "Lagerung von Waren", "img_url": "fa-solid fa-warehouse"}, {"text": "Wir übernehmen den Transport Ihrer Waren zwischen den von Ihnen angegebenen Adressen in geschlossenen Kastenwagen. Für den Transport von Unternehmen von Haus zu Haus vereinbaren Sie bitte einen Termin mit unserem Callcenter unter der Nummer 444 1980.", "title": "Transport von Haus zu Haus", "img_url": "fa-solid fa-boxes-packing"}, {"text": "Für Stückguttransporte, ob in Ankara oder in anderen Provinzen, transportieren Sie Ihre Waren mit der Sicherheit von Doğançay Evden Eve Nakliyat.", "title": "Stückgut-Transport", "img_url": "fa-solid fa-truck-arrow-right"}], "tr": [{"text": "Evinizde bulunan atmaya kıyamadığınız veya fazla malzemelerinizi Ankara ve İstanbul’da bulunan Eşya depolama merkezlerimizde saklayabilirsiniz.", "title": "Eşya Depolama", "img_url": "fa-solid fa-warehouse"}, {"text": "Eşyalarınızın Belirlediğiniz adresler arası kapalı kasa kamyonlar içerisinde taşıma hizmetini sağlamaktayız. Kurumsal evden eve nakliyat hizmeti için 444 1980 numaralı çağrı merkezinden ekspertiz randevusu oluşturun.", "title": "Evden Eve Nakliyat", "img_url": "fa-solid fa-boxes-packing"}, {"text": "Parça eşya taşıma hizmeti için İster Ankara içi isterseniz de diğer illere Doğançay Evden Eve Nakliyat güvencesi ile eşyalarınızı taşıtın.", "title": "Parça Eşya Taşıma", "img_url": "fa-solid fa-truck-arrow-right"}]}', NULL, NULL, '2023-12-08 05:12:05');

