INSERT INTO `sliders_services` (`services_id`, `services_title`, `services_detail`, `services_img`, `services_no`, `services_durum`) VALUES
	(1, 'Ağ Çözümleri', 'Profesyonel ağ çözümleri konusunda tüm ihtiyaçlarınıza cevap veriyoruz. Konusunda uzman ve tecrübeli danışmanlar aracılığıyla, ihtiyaçları en doğru şekilde analiz ediyor; Endüstriyel standartlarda en optimum projeleri kısa zamanda hayata geçiriyoruz.', 'assets/image/slider/icon-6.svg', 1, 1),
	(2, 'Penetrasyon/Sızma Testi', 'Bazı kurumlar yasalar gereği bu ve buna benzer testleri yaptırmak zorundadır, siz de onlardansınız. Sisteminizin ve çalışanlarınızın açıklarını görüp önlem almak.Herhangi bir saldırıya uğrayıp zaman ve para kaybı yaşamamak için yaptırmanız gerekir.', 'assets/image/slider/icon-5.png', 2, 1),
	(3, 'Web Sitenizi Koruyun', 'Web Siteniz; işletmeniz markanız, vitrininiz, sosyal alanınız ve dünyaya kattığınız tüm harika şeylerin merkezidir. SSL Sertifikası, Web Sitesi Güvenliği ve Web Sitesi Yedekleme ile eksiksiz korumayı hak eder. Bu, acımasız web ortamında güvende kalmanın tek yoludur.', 'assets/image/slider/icon-1.svg', 3, 1),
	(4, 'Bilgi Güvenliği Danışmanlığı', 'Önlem al, izle ve yönet. Bilgi ve verilerin kurumunuz için değeri paha biçilemez ve onları güvende tutmak ise önemli bir sorumluluk. Servet değerindeki yatırımların gerçek bir güvenlik sağlaması ise tüm beklentiniz. Güvenlik açıklarının tespit edilmesi sağlarız.', 'assets/image/slider/icon-2.png', 4, 1),
	(5, 'KVKK Danışmanlığı', 'Bazı kurumlar yasalar gereği bu ve buna benzer testleri yaptırmak zorundadır, siz de onlardansınız. Sisteminizin ve çalışanlarınızın açıklarını görüp önlem almak.Herhangi bir saldırıya uğrayıp zaman ve para kaybı yaşamamak için yaptırmanız gerekir.KVKK, kişisel verilerin işlenmesinde başta özel hayatın gizliliği olmak üzere, kişilerin temel hak ve özgürlüklerini korumak ve tüzel kişilerin yükümlülükleri hususunda uyacakları usul ve esasları düzenlemektedir. Bu kanun hükümleri, kişisel verileri işlenen gerçek kişiler ile bu verileri tamamen veya kısmen otomatik olan ya da herhangi bir veri kayıt sisteminin parçası olmak kaydı ile otomatik olmayan yollarla işleyen gerçek ve tüzel kişiler hakkında uygulanır.', 'assets/image/slider/icon-3.svg', 5, 1),
	(6, 'Sunucu Yönetimi', 'Sağlayıcı Sunucu Yönetimi, genel anlamı ile sunucu üzerinde çalışan servislerin ve yazılımların sürekli kontrol altında tutularak gerekli hallerde müdahale edilmesi, güncellemelerin uygulanması ve sunucunun doğru çalışmasının sağlanması hizmetidir. Sunucuda oluşacak herhangi bir yazılım sorununda danışmanlık hizmeti de kapsam dâhilindedir.', 'assets/image/slider/icon-4.png', 6, 1);

