-- dogancay_nakliyat.tasima_hizmetlerimizs: ~0 rows (yaklaşık) tablosu için veriler indiriliyor
INSERT INTO `tasima_hizmetlerimizs` (`id`, `data`, `deleted_at`, `created_at`, `updated_at`) VALUES
	(1, '{"en": {"data": [{"text": "Our services include transportation services for international companies, diplomatic personnel and private customers planning to move abroad. This service provides; international moving by sea, international moving by air, international moving on land, packing and moving supplies, and storage solutions both self-storage and smart-storage (in Containers).", "img_url": "assets/images/new/tasimahizmetleri/EF1rqA4wHQJ2tp3m1KeI_1699280376.jpg"}, {"text": "The means of transportation for international household and office moving depend on time consideration, your budget, and size of your goods and the location of your places both your source and destination target. Our move coordinator will offer you best reasonable and feasible mean to move your goods internationally. The means of international relocations are transportation by sea, air or land. Each of them has pros and cons regarding cost, time, availability and distance.", "img_url": "assets/images/new/tasimahizmetleri/UGN0ZPflIgrw0EB3VNCL_1699622239.jpg"}], "title": "OUR MOVING SERVICES"}, "ge": {"data": [{"text": "Unser internationaler Transportservice umfasst internationale Heim- und Bürotransporte von internationalen Unternehmen, im Ausland eingesetztem Diplomatenpersonal und Zivilpersonen. Wir bieten diesen Service einschließlich See-, Luft- und Landtransporten sowie Verpackungs-, Self-Storage- und Smart-Storage-Dienstleistungen an.", "img_url": "assets/images/new/tasimahizmetleri/EF1rqA4wHQJ2tp3m1KeI_1699280376.jpg"}, {"text": "Bei der Festlegung der Umzugsmethode im internationalen Heim- und Bürotransport wird die Entscheidung anhand einiger Kriterien getroffen. Dabei geht es um die Dauer des Umzugs, die Größe des Umzugsguts, den Stand Ihres Budgets und den physischen Standort des Umzugsorts (auf welcher Etage er sich befindet usw.). Unter Berücksichtigung dieser Bewertungen unterbreitet Ihnen unser Transportkoordinator das günstigste Preisangebot und legt die Umzugsmethode fest. Es können jedoch Alternativangebote vorgelegt werden. Diese Methoden sind Heim- und Bürotransporte auf dem See-, Luft- und Landweg. Jede Methode hat Vor- und Nachteile hinsichtlich Kosteneffizienz, Lieferzeit und Größe der Artikel.", "img_url": "assets/images/new/tasimahizmetleri/UGN0ZPflIgrw0EB3VNCL_1699622239.jpg"}], "title": "UNSERE UMZUGSDIENSTLEISTUNGEN"}, "tr": {"data": [{"text": "Uluslararası taşıma hizmetimiz uluslararası firmaların, yurtdışına görevlendirilmiş diplomatik personelin ve sivil şahısların uluslararası ev ve ofis taşıma işlemlerini kapsamaktadır. Bu hizmeti deniz, hava, ve kara yoluyla yapılan taşımacılık ile paketleme, self-storage ve akıllı depolama hizmetleri dahil olarak veriyoruz.", "img_url": "assets/images/new/tasimahizmetleri/EF1rqA4wHQJ2tp3m1KeI_1699280376.jpg"}, {"text": "Uluslararası ev ve ofis taşımacılığında taşınma metodu belirlenirken bazı kriterlere göre kara verilir. Bunlar ne kadar zamanda taşınma isteniyor, eşyaların büyüklüğü ne kadar,  bütçenizin durumu ve taşınma yerlerinin fiziksel konu(kaçıncı katta olduğu…vb). Taşıma koordinatörümüz bu değerlendirmeleri göz önüne alarak size en uygun fiyat teklifini sunacak ve taşınma metodunu belirleyecektir. Yine de alternatif teklifler sunulabilir. Bu metotlar deniz yolu, hava yolu ve kara yoluyla yapılan ev ve of taşımasıdır. Her bir metodun maliyet etkinlik, teslim süresi zamanı ve eşyaların büyüklüğü açısından avantaj ve dezavantajları bulunmaktadır.", "img_url": "assets/images/new/tasimahizmetleri/UGN0ZPflIgrw0EB3VNCL_1699622239.jpg"}], "title": "TAŞIMA HİZMETLERİMİZ"}, "hero": "assets/images/new/tasimahizmetleri/PmtqbAJuFeqwfDljRE7r_1699278059.jpg"}', NULL, NULL, '2023-11-10 10:17:19');

